package day12_Assignment_6_NestedForLoop;

public class Question_5 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		for(int i=1; i<=7; i++) {
			for(int j=1; j<=7-i; j++) {
				System.out.print("1 " );
			}				
			for(int m=1; m<=i; m++) {
				System.out.print(i + " ");
				}
				System.out.println();
				
	}
		
 }

}
